pragma solidity 0.6.12;

import "./ICryptoPairOracle.sol";

// Fixed window oracle that recomputes the average price for the entire period once every period
// Note that the price average is only guaranteed to be over at least 1 period, but may be over a longer period
contract WethToWethOracle is ICryptoPairOracle {

    address internal wethAddress;

    constructor(address _wethAddress) public {
        wethAddress = _wethAddress;
    }

    function consult(address tokenIn, uint256 amountIn) external view override returns (uint amountOut) {
        require(tokenIn == wethAddress, "This oracle only accepts consulting WETH input");

        return amountIn;
    }

    function updateOracle() external override {}

    function shouldUpdateOracle() external view override returns (bool) {
        return false;
    }

    function when_should_update_oracle_in_seconds() external view override returns (uint256) {
        return type(uint256).max;
    }
}","keccak256":"0xb7ffd5bd6a31c61832453ce4559587180d19ab1749b02635933f2562b6548285","license":"MIT"}},"version":1}
